    @staticmethod
    def retrieve_glyph(character, font=None):

        if font is None:
            font = Font.FONT_TOM_THUMB

        if character in font:
            return font[character]
        else:
            return font[" "]

    @staticmethod
    def supported_characters(font=None):

        if font is None:
            font = Font.FONT_TOM_THUMB

        return font.keys()
    
    @staticmethod
    def retrieve_glyph_height(font=None):
        if font is None:
            font = Font.FONT_TOM_THUMB
        period_length = font['.'][-1]
        if period_length == 0:
            period_length = 1
        return (len(font['.']) - 1) // period_length
    
    @staticmethod
    def retrieve_glyph_leading(character, font=None):
        if font is None:
            font = Font.FONT_TOM_THUMB
        
        char = Font.retrieve_glyph(character, font)
        if char[-1] == 0:
            return 1
        return char[-1]

